export default class Render {

	splitByNumber(str, size) {
	  const numChunks = Math.ceil(str.length / size)
	  const chunks = new Array(numChunks)

	  for (let i = 0, o = 0; i < numChunks; ++i, o += size) {
	    chunks[i] = str.substr(o, size)
	  }

	  return chunks
	}

	text(str, x, y, size, ctx, color= "white") {
		if (str.toString().length > 6 && parseInt(size) >= 10) {
			x -= 6
		}
		str = str.toString().split(' ');

		ctx.strokeStyle = color
		ctx.font = size+"px Comic Sans MS";

		for (let word in str) {
			ctx.strokeText(str[word], x, y + (word * 7));
		}
    }

    textLine(str, x, y, size, ctx, color= "white") {
		if (str.toString().length > 6 && parseInt(size) >= 10) {
			x -= 6
		}

		ctx.strokeStyle = color
		ctx.font = size+"px Comic Sans MS";

		ctx.strokeText(str, x, y);

    }

    textLinesByLength(str, x, y, size, numToSplit, ctx, color= "white") {
    	let strChunks = this.splitByNumber(str, numToSplit);
    	ctx.strokeStyle = color
		ctx.font = size+"px Comic Sans MS";
		console.log(strChunks)
		let y_offset = 0;
		for (let line in strChunks) {
			console.log(line)
			console.log(str[line] + " newline")
			ctx.strokeText(str[line], x, y_offset += 10);
		}
    }

    img(img, x,y, width, height, ctx) {
	    ctx.drawImage(img.img, 
	                  img.pos.x,
	                  img.pos.y, 
	                  img.pos.width, 
	                  img.pos.height,
	                  x,
	                  y,
	                  width,
	                  height
	                );
	}

	roundRect(x, y, w, h, r, ctx) {
		ctx.fillStyle = 'rgba(16, 133, 26,0.9)';
	  if (w < 2 * r) r = w / 2;
	  if (h < 2 * r) r = h / 2;
	  ctx.beginPath();
	  ctx.moveTo(x+r, y);
	  ctx.arcTo(x+w, y,   x+w, y+h, r);
	  ctx.arcTo(x+w, y+h, x,   y+h, r);
	  ctx.arcTo(x,   y+h, x,   y,   r);
	  ctx.arcTo(x,   y,   x+w, y,   r);
	  ctx.closePath();;
	}
}
